sys_ui_action.script:
if (current.u_easy_url.indexOf("goo.gl") >= 0) {
    var type = 'get';
} else {
    var type = 'post';
}
try {
	gs.info('shortener running','LK@SSV');
    var r = new sn_ws.RESTMessageV2('Lab 4 - Google URL Shortener', type);
    r.setStringParameter('url', current.u_easy_url);
    var response = r.execute();
    var responseBody = response.getBody();
    var httpStatus = response.getStatusCode();
	gs.info('shortener running before if','LK@SSV');
    if (httpStatus == '200') {
        var jp = new JSONParser();
        var responseObj = jp.parse(responseBody);
        var newurl = responseObj;
        if (type == 'get') {
            current.u_easy_url = newurl.longUrl;
        } else {
            current.u_easy_url = newurl.id;
        }
		gs.info('shortener running if done','LK@SSV');
    }
    current.update();
    action.setRedirectURL(current);
} catch (ex) {
    var message = ex.getMessage();
}
